import React, { useState, useEffect } from 'react';

function App() {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const clientId = '648055946887-4er5sea1ghchnroe19sf761l6dtmme4i.apps.googleusercontent.com'; // Replace with your client ID
  const developerKey = 'AIzaSyCciSnW0Ap8k_Mxz3kLXC-EQ_d5adr415I'; // Replace with your API key

  const handleOpenPicker = () => {
    setLoading(true);
    const script = document.createElement('script');
    script.src = 'https://apis.google.com/js/api.js';
    script.onload = () => {
      gapi.load('picker', () => {
        const picker = new google.picker.PickerBuilder()
          .addView(google.picker.ViewId.DOCS)
          .setOAuthToken(null)
          .setDeveloperKey(developerKey)
          .setCallback((data) => {
            if (data.action === google.picker.Action.PICKED) {
              const file = data.docs[0];
              setFiles([file]);
            }
          })
          .build();
        picker.setVisible(true);
      });
    };
    script.onerror = () => {
      console.error('Error loading Google Drive API script');
    };
    document.body.appendChild(script);
  };

  return (
    <div>
      <button onClick={handleOpenPicker}>Open Google Drive Picker</button>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {files && files.length > 0 ? (
            files.map((file) => (
              <p key={file.id}>{file.name}</p>
            ))
          ) : (
            <p>No files selected</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
