import React, { useState, useEffect } from 'react';
import axios from 'axios';
import useDrivePicker from 'react-google-drive-picker';

const CLIENT_ID = 'YOUR_CLIENT_ID';
const REDIRECT_URI = 'http://localhost:3000/callback'; // Your redirect URI
const AUTH_ENDPOINT = 'https://accounts.spotify.com/authorize';
const RESPONSE_TYPE = 'code';
const SCOPE = 'user-top-read';

function App() {
  const [openPicker, data, authResponse] = useDrivePicker();
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [spotifyData, setSpotifyData] = useState([]);
  const [token, setToken] = useState('');

  const handleOpenPicker = () => {
    setLoading(true);
    openPicker({
      clientId: "648055946887-4er5sea1ghchnroe19sf761l6dtmme4i.apps.googleusercontent.com",
      developerKey: "AIzaSyCciSnW0Ap8k_Mxz3kLXC-EQ_d5adr415I",
      viewId: "DOCS",
      showUploadView: true,
      showUploadFolders: true,
      supportDrives: true,
      multiselect: true,
    })
    .then((data) => {
      setLoading(false);
      setFiles(data.docs);
    })
    .catch((error) => {
      setLoading(false);
      setError(error);
      console.error("Error opening picker: ", error);
    });
  };

  useEffect(() => {
    if (data) {
      setLoading(false);
      setFiles(data.docs);
    }
  }, [data]);

  useEffect(() => {
    const fetchToken = async (code) => {
      try {
        const tokenResponse = await axios.post('https://accounts.spotify.com/api/token', new URLSearchParams({
          grant_type: 'authorization_code',
          code: code,
          redirect_uri: REDIRECT_URI,
          client_id: CLIENT_ID,
          client_secret: 'YOUR_CLIENT_SECRET'
        }));

        setToken(tokenResponse.data.access_token);
      } catch (error) {
        console.error("Error fetching access token: ", error);
        setError(error);
      }
    };

    const code = new URLSearchParams(window.location.search).get('code');
    if (code) {
      fetchToken(code);
    }
  }, []);

  useEffect(() => {
    const fetchSpotifyData = async () => {
      if (token) {
        try {
          const response = await axios.get('https://api.spotify.com/v1/me/top/artists', {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });
          setSpotifyData(response.data.items);
        } catch (error) {
          console.error("Error fetching Spotify data: ", error);
          setError(error);
        }
      }
    };

    fetchSpotifyData();
  }, [token]);

  const handleLogin = () => {
    window.location.href = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`;
  };

  return (
    <div>
      <button onClick={handleOpenPicker}>Open Google Drive Picker</button>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {files && files.length > 0 ? (
            files.map((file) => (
              <p key={file.id}>{file.name}</p>
            ))
          ) : (
            <p>No files selected</p>
          )}
        </div>
      )}
      <div>
        {!token ? (
          <button onClick={handleLogin}>Login to Spotify</button>
        ) : (
          spotifyData.length > 0 ? (
            spotifyData.map((artist) => (
              <div key={artist.id} style={{ marginBottom: '20px' }}>
                <h2>{artist.name}</h2>
                <img src={artist.images[0]?.url} alt={artist.name} style={{ width: '200px', height: '200px' }} />
                <p>Followers: {artist.followers.total}</p>
                <p>Popularity: {artist.popularity}</p>
                <p>Genres: {artist.genres.join(', ')}</p>
              </div>
            ))
          ) : (
            error && <p>Error fetching Spotify data: {error.message}</p>
          )
        )}
      </div>
    </div>
  );
}

export default App;
